number_charweaponfullattack.script:
function onDoubleClick(x,y)
	local nodeAttack = getDatabaseNode().getParent();

	local bNotLoaded = (DB.getValue(nodeAttack, 'isloaded') == 0);
	if window.isloaded.hasLoadAction() and bNotLoaded then
		local nodeActor = nodeAttack.getChild('...');
		local sActorName = DB.getValue(nodeActor, 'name', 'Someone');
		local sWeaponName = DB.getValue(nodeAttack, 'name', 'ranged weapon');

		ChatManager.Message(string.format(Interface.getString('char_actions_notloaded'), sActorName, sWeaponName));
	end

	DB.setValue(nodeAttack, 'isloaded', 'number', 0);

	return action();
end

number_charweaponattacktotal.script:
function onDoubleClick(x,y)
